mapper.update:
update p_shelf

    station=#{stationId},
    capacity=#{capacity},
    stock=#{stock}

where id=#{id}

update p_shelf

    set
    capacity=capacity+stock-#{item.stock},
    stock=#{item.stock}
    where id=#{item.stationId}

update p_shelf set
    stock=stock+#{value}
where station=#{station} and no=#{no} and (stock+#{value})<=capacity
